steps.script:
if doc.server_type == "Database Server":
    default_variables = frappe.get_all("MariaDB Variable", {"set_on_new_servers":1}, pluck="name")
    for var_name in default_variables:
        var = frappe.get_doc("MariaDB Variable", var_name)
        var.set_on_server(doc.server)

server = frappe.get_doc(doc.server_type, doc.server)
server.set_swappiness()

if doc.server_type == "Server":
    server.setup_mysqldump()
    server.install_earlyoom()

if doc.server_type == "Database Server":
    server = frappe.get_doc(doc.server_type, doc.server)
    server.adjust_memory_config()
