steps.script:
if doc.server_type == "Database Server":
    server = frappe.get_doc("Database Server", doc.server)
    server.upgrade_mariadb()

if doc.server_type == "Database Server":
    plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Upgrade MariaDB"}, ["status"], order_by="creation desc", limit=1)
    result = (plays and plays[0].status == "Success", plays and plays[0].status == "Failure")
else:
    result = (True,)

if doc.server_type == "Database Server":
    server = frappe.get_doc("Database Server", doc.server)
    server.upgrade_mariadb_patched()

if doc.server_type == "Database Server":
    plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Upgrade MariaDB Patched"}, ["status"], order_by="creation desc", limit=1)
    result = (plays and plays[0].status == "Success", plays and plays[0].status == "Failure")
else:
    result = (True,)

if doc.server_type == "Database Server":
    default_variables = frappe.get_all("MariaDB Variable", {"set_on_new_servers":1}, pluck="name")
    for var_name in default_variables:
        var = frappe.get_doc("MariaDB Variable", var_name)
        var.set_on_server(doc.server)

server = frappe.get_doc(doc.server_type, doc.server)
server.set_swappiness()

if doc.server_type == "Server":
    server.setup_mysqldump()
    server.install_earlyoom()

machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)
try:
    machine.start()
    result = (True, False)
except:
    result = (False, False)

if doc.server_type == "Database Server":
    server = frappe.get_doc(doc.server_type, doc.server)
    server.adjust_memory_config()

machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)
try:
    machine.start()
    result = (True, False)
except:
    result = (False, False)
