steps.script:
server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type == "Server":
    server.scale_up()

callback_script:
snapshot = frappe.get_doc("Server Snapshot", arguments.get("server_snapshot"))
if doc.server_type == "Server":
	snapshot.resume_app_server_services()
elif doc.server_type == "Database Server":
	snapshot.resume_database_server_services()

if doc.status == "Failure":
    frappe.db.set_value("Server Snapshot", snapshot.name, "status", "Failure", update_modified=False)
    for s in snapshot.snapshots:
        try:
            frappe.get_doc("Virtual Disk Snapshot", s).delete_snapshot()
        except:
            pass

else:
    snapshot.sync(now=False)

steps.script:
machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)

while True:
    try:
        machine.sync()
    finally:
        break

if machine.status == "Terminated":
    raise Exception("Can't snapshot terminated virtual machine")
elif machine.status == "Draft":
    raise Exception("Can't snapshot draft virtual machine")

if doc.server_type == "Server" and arguments.get("is_consistent_snapshot", False):
    server = frappe.get_doc("Server", doc.server)
    output = server.ansible_run("systemctl stop docker")
    if not (output and output.get("status") == "Success"):
        raise Exception("Failed to stop docker daemon")
else:
    result = (False, False) # Skipped

if doc.server_type == "Database Server" and arguments.get("is_consistent_snapshot", False):
    server = frappe.get_doc("Database Server", doc.server)
    output = server.ansible_run("systemctl stop mariadb")
    if not (output and output.get("status") == "Success"):
        raise Exception("Failed to stop mariadb service")
    result = (True, False)
else:
    result = (False, False) # Skipped

machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)
print(machine.as_dict())
if machine.cloud_provider in ["AWS EC2", "OCI"]:
    machine.attach_new_volume(machine.size, machine.iops, machine.throughput)
else:
    machine.attach_volume(size=100)

machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)
try:
    machine.start()
    result = (True, False)
except:
    result = (False, False)

callback_script:
server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type in ["Server", "Database Server"] and server.is_for_recovery:
    filter_field = "app_server" if doc.server_type == "Server" else "database_server"
    recovery_record_name = frappe.db.get_value("Server Snapshot Recovery", {filter_field: doc.server}, "name")
    if recovery_record_name:
        recovery_record = frappe.get_doc("Server Snapshot Recovery", recovery_record_name, for_update=True)

        if doc.status == "Success":
            if doc.server_type == "Server":
                recovery_record.app_server_archived = True
            else:
                recovery_record.database_server_archived = True
            recovery_record.save()

steps.script:
should_auto_increase = frappe.db.get_value(doc.server_type, doc.server, "auto_increase_storage")
if not should_auto_increase:
    result = (True, False)

else:
    if frappe.db.get_value(doc.server_type, doc.server, "provider") == "AWS EC2":
        plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Extend EC2 Volume"}, ["status"], order_by="creation desc", limit=1)
        result = (plays and plays[0].status == "Success", plays and plays[0].status == "Failure")
    else:
        result = (True, False)

server = frappe.get_doc(doc.server_type, doc.server)
if server.provider == "OCI":
    server.add_glass_file()
else:
    result = (True, False)
    # handled for aws already in extend playbook

if doc.server_type == 'Server':
    server = frappe.get_doc(doc.server_type, doc.server)
    server.start_active_benches()

machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)
try:
    machine.start()
    result = (True, False)
except:
    result = (False, False)

server = frappe.get_doc(doc.server_type, doc.server)

if server.use_for_build:
    server.prune_docker_system()

provider = frappe.db.get_value(doc.server_type, doc.server, 'provider')
if provider == 'Hetzner' and doc.server_type != "Proxy Server":
    plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Mount Volumes"}, ["status"], order_by="creation desc", limit=1)
    result = (plays and plays[0].status == "Success", False)
else:
    result = (True, False)

server = frappe.get_doc(doc.server_type, doc.server)
if server.provider == "Hetzner" :
    if server.doctype == "Server":
        server.setup_docker()
    elif server.doctype == "Database Server":
        server.set_mariadb_mount_dependency()

if doc.server_type == "Database Server":
    server = frappe.get_doc("Database Server", doc.server)
    server.upgrade_mariadb()

if doc.server_type == "Database Server":
    plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Upgrade MariaDB"}, ["status"], order_by="creation desc", limit=1)
    result = (plays and plays[0].status == "Success", plays and plays[0].status == "Failure")
else:
    result = (True,)

if doc.server_type == "Server" and arguments.get("is_consistent_snapshot", False):
    server = frappe.get_doc("Server", doc.server)
    output = server.ansible_run("systemctl start docker")
    if not (output and output.get("status") == "Success"):
        raise Exception("Failed to start docker daemon")

    frappe.db.set_value("Server Snapshot", arguments.get("server_snapshot"), "app_server_services_started", True, update_modified=False)
else:
    result = (False, False) # Skipped
    # As it wasn't a consistent snapshot services were never stopped
    frappe.db.set_value("Server Snapshot", arguments.get("server_snapshot"), "app_server_services_started", True, update_modified=False)

if doc.server_type == "Database Server" and arguments.get("is_consistent_snapshot", False):
    server = frappe.get_doc("Database Server", doc.server)
    output = server.ansible_run("systemctl start mariadb")
    if not (output and output.get("status") == "Success"):
        raise Exception("Failed to start mariadb service")
    frappe.db.set_value("Server Snapshot", arguments.get("server_snapshot"), "database_server_services_started", True, update_modified=False)

else:
    result = (False, False) # Skipped
    # As it wasn't a consistent snapshot services were never stopped
    frappe.db.set_value("Server Snapshot", arguments.get("server_snapshot"), "database_server_services_started", True, update_modified=False)

server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type == "Registry Server":
    server.prune_mirror_registry()

server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type == "Server" and not server.scaled_up:
    server.scale_up(is_automatically_triggered=True)

server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type == "Server" and server.scaled_up:
    server.scale_down(is_automatically_triggered=True)

machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)
try:
    machine.start()
    result = (True, False)
except:
    result = (False, False)

server = frappe.get_doc(doc.server_type, doc.server)

if getattr(server, "is_unified_server", False):
    server_doc = frappe.get_doc("Server", doc.server)
    db_doc = frappe.get_doc("Database Server", doc.server)

    server_doc.auto_scale_workers()
    db_doc.adjust_memory_config()

else:
    if doc.server_type == "Database Server":
        server.adjust_memory_config()

    elif doc.server_type == "Server":
        server.auto_scale_workers()

machine = frappe.get_doc("Virtual Machine", doc.virtual_machine)

server = frappe.get_doc(doc.server_type, doc.server)
if server.plan:
    plan_disk_size = frappe.db.get_value("Server Plan", server.plan, "disk")
    if plan_disk_size and plan_disk_size > machine.disk_size:
        try:
            server.increase_disk_size(increment=plan_disk_size - machine.disk_size)
        except:
            pass

callback_script:
server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type in ["Server", "Database Server"] and server.is_for_recovery:
    filter_field = "app_server" if doc.server_type == "Server" else "database_server"
    recovery_record_name = frappe.db.get_value("Server Snapshot Recovery", {filter_field: doc.server}, "name")
    if recovery_record_name:
        recovery_record = frappe.get_doc("Server Snapshot Recovery", recovery_record_name, for_update=True)

        if doc.status == "Success":
            if doc.server_type == "Server":
                recovery_record.is_app_server_ready = True
            else:
                recovery_record.is_database_server_ready = True
            recovery_record.save()
        else:
            recovery_record.mark_server_provisioning_as_failed()

if doc.server_type in ["Server", "Database Server"] and "logical_replication_backup" in arguments:
    if doc.status == "Success":
        frappe.get_doc("Logical Replication Backup", arguments.get("logical_replication_backup")).next()
    if doc.status == "Failure":
        frappe.get_doc("Logical Replication Backup", arguments.get("logical_replication_backup")).fail()

if doc.server_type in ["Server", "Database Server"] and doc.status == "Success":
    server.is_provisioning_press_job_completed = 1
    server.save(ignore_permissions=True)

    if server.is_unified_server:
        frappe.db.set_value("Database Server", server.database_server, "is_provisioning_press_job_completed", 1, update_modified =False)

steps.script:
try:
    vm = frappe.get_doc("Virtual Machine", doc.virtual_machine)
    vm.sync()
    if len(vm.volumes) > 0:
        result = (True, False)
    else:
        result = (False, False)
except (frappe.QueryDeadlockError, frappe.QueryTimeoutError, frappe.TimestampMismatchError):
    result = (False, False)
except Exception as e:
    raise e

vm = frappe.get_doc("Virtual Machine", doc.virtual_machine)
if vm.data_disk_snapshot:
    is_created = vm.create_data_disk_volume_from_snapshot()
    if is_created:
        result = (True, False)
    else:
        arguments.update({"max_volume_creation_retries": arguments.get("max_volume_creation_retries", 6)-1})
        if arguments.get("max_volume_creation_retries") <= 0:
            result = (False, True)
        result = (False, False)

vm = frappe.get_doc("Virtual Machine", doc.virtual_machine)
if vm.data_disk_snapshot:
    is_attached = vm.check_and_attach_data_disk_snapshot_volume()
    if is_attached:
        result = (True, False)
    else:
        result = (False, False)

server = frappe.get_doc(doc.server_type, doc.server)
if server.provider == "AWS EC2" and frappe.db.get_value("Virtual Machine", server.virtual_machine, "data_disk_snapshot"):
    try:
        vm = frappe.get_doc("Virtual Machine", server.virtual_machine)
        vm.sync()

        if len(vm.volumes) == 0 or (vm.data_disk_snapshot_attached and len(vm.volumes) == 1):
            result = (False, False)
        else:
            server.reload()
            server.validate_mounts()
            server.save()
            result = (True, False)
    except (frappe.QueryDeadlockError, frappe.QueryTimeoutError, frappe.exceptions.TimestampMismatchError):
        result = (False, False)
    except Exception as e:
        raise e
else:
    result = (True, False)

server = frappe.get_doc(doc.server_type, doc.server)
if server.provider == "AWS EC2" and frappe.db.get_value("Virtual Machine", server.virtual_machine, "data_disk_snapshot"):
    plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Mount Volumes"}, ["status"], order_by="creation desc", limit=1)
    result = (plays and plays[0].status == "Success", plays and plays[0].status == "Failure")
else:
    result = None

provider = frappe.db.get_value(doc.server_type, doc.server, 'provider')
if provider == "Hetzner" and doc.virtual_machine:
    vm = frappe.get_doc("Virtual Machine", doc.virtual_machine)
    server = frappe.get_doc(doc.server_type, doc.server)

    if vm.virtual_machine_image:
        vmi = frappe.get_doc("Virtual Machine Image", vm.virtual_machine_image)

        if vmi.has_data_volume:
            # If VMI has data volume, then only proceed with attaching disk
            if server.plan:
                data_disk_size = int(frappe.db.get_value("Server Plan", server.plan ,"disk"))
            else:
                data_disk_size = 25

            vm.attach_new_volume(data_disk_size)

            # Do Virtual Machine Sync
            # Until we got no error
            max_sync_tries = 100
            while max_sync_tries:
                try:
                    vm.sync()
                    break
                except Exception as e:
                    max_sync_tries = max_sync_tries - 1
                    if max_sync_tries <=0 :
                        raise e

            server.validate_mounts()
            server.save(ignore_version=True) # To avoid timestamp mismatch errors
            server.mount_volumes(now=False)
            result = (True, False)
        else:
            result = (False, False)

provider = frappe.db.get_value(doc.server_type, doc.server, 'provider')
if provider == "Hetzner" and doc.virtual_machine:
    vm = frappe.get_doc("Virtual Machine", doc.virtual_machine)

    if vm.virtual_machine_image:
        vmi = frappe.get_doc("Virtual Machine Image", vm.virtual_machine_image)
        if vmi.has_data_volume:
            # Check for running ansible play
            plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Mount Volumes"}, ["status"], order_by="creation desc", limit=1)
            result = (plays and plays[0].status == "Success", False)
        else:
            result = None

server = frappe.get_doc(doc.server_type, doc.server)
if server.provider == "Hetzner" and server.has_data_volume:
    if server.doctype == "Server":
        server.setup_docker()
    elif server.doctype == "Database Server":
        server.set_mariadb_mount_dependency()
else:
    result = (False, False)

play_statuses = frappe.db.get_all("Ansible Play", filters={
    "server_type": doc.server_type,
    "server": doc.server,
    "play": ("in", ["Install Docker", "Setup MariaDB Mount Dependency"])
}, pluck="status")

all_completed = True
is_failure = False

for status in play_statuses:
    if status == "Failure":
        is_failure = True

    all_completed = all_completed and status == "Success"

if all_completed:
    result = (True, False)
elif is_failure:
    result = (False, True)
else:
    result = (False, False)

if doc.server_type == "Database Server" and arguments.get(
	"setup_db_replication", False
):
	pass

if doc.server_type == "Database Server":
    server = frappe.get_doc("Database Server", doc.server)
    server.upgrade_mariadb()

if doc.is_unified_server:
    database_server = frappe.get_doc("Database Server", doc.database_server)
    database_server.upgrade_mariadb()

if doc.server_type == "Database Server" or doc.is_unified_server:
    plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Upgrade MariaDB"}, ["status"], order_by="creation desc", limit=1)
    result = (plays and plays[0].status == "Success", plays and plays[0].status == "Failure")
else:
    result = (True,)

server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type == "Database Server" and arguments.get(
	"setup_db_replication", False
):
	server.prepare_mariadb_replica()
	result = (True, False)
else:
	result = (False, False)

if doc.server_type == "Database Server" and arguments.get("setup_db_replication", False):
    plays = frappe.get_all("Ansible Play", {"server": doc.server, "play": "Prepare MariaDB Replica"}, ["status"], order_by="creation desc", limit=1)
    result = (plays and plays[0].status == "Success", plays and plays[0].status == "Failure")
else:
    result = (True,)

server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type == "Database Server" and arguments.get(
	"setup_db_replication", False
):
	server.configure_replication()
	result = (True, False)
else:
	result = (False, False)

server = frappe.get_doc(doc.server_type, doc.server)

if doc.server_type == "Database Server" and arguments.get(
	"setup_db_replication", False
):
	server.start_replication()
	result = (True, False)
else:
	result = (False, False)

if doc.server.startswith("fs") and doc.server_type == "Server":
    primary_server = frappe.db.get_value("Server", doc.server, "primary")
    nfs_volume_attachment = frappe.get_doc(
	    {"doctype": "NFS Volume Attachment", "primary_server": primary_server}
    )
    nfs_volume_attachment.insert(ignore_permissions=True)
    frappe.db.commit()
